sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

 (function() {
    // Get the value of the 'Date of Joining' field
    var dateOfJoining = g_form.getValue('date_of_joining');

    // Get today's date
    var today = new Date();

    // Check if the selected date is in the past
    if (new Date(dateOfJoining) < today) {
        // Display error message
        g_form.addErrorMessage('Please select a future date for the Date of Joining.');

        // Clear the field value
        g_form.setValue('date_of_joinin','');
    }
})();

}
